import React from "react";
import { Header, Table,Form, Button } from 'semantic-ui-react'
import {Link} from "react-router-dom";
import PageWrapper from "../components/PageWrapper";
export const BASE_URL = 'https://reast-api-b141b.firebaseio.com/';
const InitialFormState =
                {
                 name:"",
                ingridients:"",
                price:0
            }
export default class Burgers extends React.Component{
    constructor(props){
        super(props)
        this.state={
            burgers:[],
            form:{
                name:"",
                ingridients:"",
                price:0
            }
            }
        }
    handleOnChange=(event)=>{

            this.setState({
                form:{
                    ...this.state.form,
                    [event.target.name]:event.target.value
                }
            })
    };
    handleOnClick=(event)=>{
        event.preventDefault();
        fetch(`${BASE_URL}/burgers.json`,{
            method: "POST",
            body: JSON.stringify(this.state.form)
        })
        .then(()=>{  
            this.fetchBurgers();
            this.setState({
                form: InitialFormState
            })
        }
        )

    }
    fetchBurgers(){
        fetch(`${BASE_URL}/burgers.json`)
        .then(resp=>resp.json())
        .then(resp=>this.setState({
            burgers: Object.keys(resp).map(i=>({ ...resp[i], id: i }))
        }))
        }
    componentDidMount(){
        this.fetchBurgers()
    }
    render(){
        const {burgers,form}= this.state
        return(
                <PageWrapper>

                <Table  basic='very' celled collapsing>
                <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Name</Table.HeaderCell>
                    <Table.HeaderCell>Ingridients</Table.HeaderCell>
                    <Table.HeaderCell>price</Table.HeaderCell>
                </Table.Row>
                </Table.Header>
                <Table.Body>
                {burgers.map(burger=>(
                    <Table.Row key={burger.id}>
                    <Table.Cell>
                      <Header as='h4'>
                            <Link to={`burgers/${burger.id}`}>
                            <Header.Content>
                            {burger.name}
                             </Header.Content>
                          </Link>
                      </Header>
                    </Table.Cell>
                    <Table.Cell>{burger.ingridients}</Table.Cell>
                    <Table.Cell>{burger.price}</Table.Cell>
                    <Table.Cell><Button color="purple">Edit</Button></Table.Cell>
                    <Table.Cell><Button color="red">Delete</Button></Table.Cell>
                  </Table.Row>
                ))}
                </Table.Body>
                </Table>
                  <Form>
                  <Form.Field>
                    <label>Name</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.name}
                    name="name"
                    type="string"
                    placeholder='Name' />
                  </Form.Field>
                  <Form.Field>
                    <label>Ingridients</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.ingridients}
                    name="ingridients"
                    type="string"
                    placeholder='Ingridients' />
                  </Form.Field>
                  <Form.Field>
                    <label>Price</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.price}
                    name="price"
                    type="number"
                    placeholdel="price"></input>
                  </Form.Field>
                  <Button type='submit' onClick={this.handleOnClick}>Add</Button>
                </Form>
                </PageWrapper>
        )
    }
}
