import React from "react";
import {  Card, Dimmer, Loader, Image, Segment  } from 'semantic-ui-react'

import GameCard from "./game-card/GameCard.js";

export class GameCardColection extends React.Component{
    constructor(props){
        super(props)
        this.state={
            games:[],
            loading: true,
            error: null,
        }
    }
    componentDidMount(){
        this.fetchData()
    }
    fetchData(){
        fetch("/plays.json")
            .then(resp=>resp.json())
            .then(resp=>
               this.setState({
                    games: resp,
                    loading:false
               })) 
            .catch(error=>this.setState({
                error:"err occ"
            }))  
    }
    displayGameKind(){
            return(this.state.games.map(game=>
              <GameCard
              id={game.id}
              title={game.title}
              localization={game.localization.place}
              date={game.date}
              playerMax={game.palyer.max}  
              playerCur={game.palyer.current}  
              reqLvl={game.ReqLevelID}
              descript={game.Description}

              />
                  )
            )}
    render(){
      if(this.state.loading){
        return(
          <Segment>
          <Dimmer active>
            <Loader size='massive'>Loading</Loader>
          </Dimmer>

          <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
          <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
          <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
        </Segment>
        )
      }else return(

            <div>
                <Card.Group margin="12px">
                  {this.displayGameKind()}
                </Card.Group>
            </div>
        )
    }
}
