        this.state={
            type:"",
        }
        this.gameTypeNames= this.gameTypeNames.bind(this)
    }
componentDidMount(){
   this.fetchTypeOfGames()
}

fetchTypeOfGames(){
    fetch("./data/kinds-of-games.json")
    .then(res=>res.json())
            .then(res=>
               this.setState({
                    type: res

               }))
}
gameTypeNames=() => this.state.type.map(type=>(type.name))
