import React from "react";
import {Link} from "react-router-dom";
import {Table, Header,Button, Icon} from "semantic-ui-react"
import { BASE_URL } from "./Burgers";

export default ({burger, onDelete})=>{
    const handleOnDeleteClick =() =>{
        fetch(`${BASE_URL}/burgers/${burger.id}.json`,{
            method: "DELETE"
        }).then(()=>{
            onDelete();
        })
    }
    return(
        <Table.Row>
        <Table.Cell>
          <Header as='h4'>
                <Link to={`burgers/${burger.id}`}>
                <Header.Content>
                {burger.name}
                 </Header.Content>
              </Link>
          </Header>
        </Table.Cell>
        <Table.Cell>{burger.ingridients}</Table.Cell>
        <Table.Cell>{burger.price}</Table.Cell>
        <Table.Cell>
            <Button color="purple" icon >
                <Icon name="edit"/>
                </Button>
            </Table.Cell>
            <Table.Cell>
            <Button color="red" icon reversed onClick={handleOnDeleteClick}>
                <Icon name="trash"/>
                </Button>
            </Table.Cell>
      </Table.Row>
    )
}
