import React from "react";

export default class GameCard extends React.Component{
    constructor(){
        super()
        this.state={
            games:[],
            loading: false,
            error: null
        }
    }
    componentDidMount(){
        this.fetchData()
    }
    fetchData(){
        fetch("/data/kids-of-games.json")
            .then(resp=>resp.json())
            .then(resp=>
               this.setState({
                    games: resp
               })) 
            .catch(error=>this.setState({
                error:"err occ"
            }))  
    }

    displayGameKind(){
        console.log(this.state.games)
            this.state.games.map(
                game=>
            <div>
                <p>{game.kindID}</p>
                <p>{game.kindName}</p>
            </div>

        )
    }
    render(){
        return(
            <div>
                {this.state.error?<p>Error Occured</p>:null}

                <h2>Game card</h2>
                {this.displayGameKind()}


            </div>
        )
    }
}
