import React from "react";
import { Form, Button } from 'semantic-ui-react'

import PageWrapper from "../components/PageWrapper";
import BurgersTable from "./BurgersTable";

export const BASE_URL = 'https://reast-api-b141b.firebaseio.com/';
const InitialFormState =
                {
                 name:"",
                ingridients:"",
                price:0
            }
export default class Burgers extends React.Component{
    constructor(props){
        super(props)
        this.state={
            burgers:[],
            form:{
                name:"",
                ingridients:"",
                price:0
            }
            }
        }
    handleOnChange=(event)=>{
            this.setState({
                form:{
                    ...this.state.form,
                    [event.target.name]:event.target.value
                }
            })
    };
    handleOnClick=(event)=>{
        event.preventDefault();
        const {form}=this.state;
        const formattedData = {
            ...form,
            price: parseInt(form.price)
        }
        fetch(`${BASE_URL}/burgers.json`,{
            method: "POST",
            body: JSON.stringify(formattedData)
        })
        .then(()=>{  
            this.fetchBurgers();
            this.setState({
                form: InitialFormState
            })
        }
        )
    }
    fetchBurgers(){
        fetch(`${BASE_URL}/burgers.json`)
        .then(resp=>resp.json())
        .then(resp=>this.setState({
            burgers: Object.keys(resp).map(i=>({ ...resp[i], id: i }))
        }))
        }
    componentDidMount(){
        this.fetchBurgers()
    }
    render(){
        const {burgers,form}= this.state
        return(
                <PageWrapper>
                <BurgersTable burgers={burgers}/>

                  <Form>
                  <Form.Field>
                    <label>Name</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.name}
                    name="name"
                    type="string"
                    placeholder='Name' />
                  </Form.Field>
                  <Form.Field>
                    <label>Ingridients</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.ingridients}
                    name="ingridients"
                    type="string"
                    placeholder='Ingridients' />
                  </Form.Field>
                  <Form.Field>
                    <label>Price</label>
                    <input 
                    onChange={this.handleOnChange}
                    value={form.price}
                    name="price"
                    type="number"
                    placeholdel="price"></input>
                  </Form.Field>
                  <Button type='submit' onClick={this.handleOnClick}>Add</Button>
                </Form>
                </PageWrapper>
        )
    }
}
