import React from "react";
import { Form,Table,Button,Icon } from "semantic-ui-react";

export default class BurgersEditRow extends React.Component{
    state={
        name:"",
        ingridients:"",
        price:0
    }

    handleOnChange=(event)=>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    handleOnSaveClick=()=>{

    }
    handleOnCloseClick=()=>{

    }

render(){
    return(
        <Table.Row>
        <Table.Cell>
            <Form>
              <Form.Field>
              <input name={"name"}
                onChange={this.handleOnChange}></input>
              </Form.Field>
              </Form>
        </Table.Cell>
        <Table.Cell>
        <Form>
            <Form.Field>
                <input name={"ingridients"}
                onChange={this.handleOnChange}></input>
                </Form.Field>
                </Form>
                </Table.Cell>
        <Table.Cell>
        <Form>
            <Form.Field>
                <input name={"price"}
                onChange={this.handleOnChange}></input>
                </Form.Field>
                </Form>
                </Table.Cell>
        <Table.Cell>
            <Button  icon success onClick={this.handleOnSaveClick} >
                <Icon name="save"/>
                </Button>
            </Table.Cell>
            <Table.Cell>
            <Button color="red" icon onClick={this.handleOnCloseClick}>
                <Icon name="close"/>
                </Button>
            </Table.Cell>
      </Table.Row>
    )
}
}
