import React from "react";
import { Card, Loader } from 'semantic-ui-react'

import {BASE_URL} from "./Burgers"

class BurgerDetails extends React.Component{
    state={
        data:null,
        loading:true
    };
    componentDidMount(){
        const {match:{params:{id}}}= this.props;
        fetch(`${BASE_URL}/burgers/${id}`)
        .then(resp=>resp.json())
        .then(resp=>{
            this.setState({
                data: resp,
                loading:false
            })
        })
    };
render(){
    const {data, loading} = this.state;
    if(loading){
        <Loader/>
    }
    return(
    <div>
         <h1>Burgers Details</h1>
         <Card>

    <Card.Content>
      <Card.Header>{data.name}</Card.Header>
      <Card.Meta>
        {data.price}
      </Card.Meta>
      <Card.Description>
       {data.ingridients}
      </Card.Description>
    </Card.Content>
    <Card.Content extra>
    </Card.Content>
  </Card>
    </div>

    )
}
}
export default BurgerDetails;
