import React from "react";
import { Header, Table, Container, Button } from 'semantic-ui-react'
import {Link} from "react-router-dom";
const BASE_URL = 'https://reast-api-b141b.firebaseio.com/'

export default class Burgers extends React.Component{
    constructor(props){
        super(props)
        this.state={
            burgers:[]
        }
    }
    fetchBurgers(){
        fetch(`${BASE_URL}/burgers.json`)
        .then(resp=>resp.json())
        .then(resp=>this.setState({
            burgers: Object.keys(resp).map(i=>({ ...resp[i], id: i }))
        }))
        }
    componentDidMount(){
        this.fetchBurgers()
    }
    render(){
        return(
            <Container style="center" textAlign="center">
                <Table basic='very' celled collapsing>
                <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Name</Table.HeaderCell>
                    <Table.HeaderCell>Ingridients</Table.HeaderCell>
                    <Table.HeaderCell>price</Table.HeaderCell>
                </Table.Row>
                </Table.Header>
                <Table.Body>
                {this.state.burgers.map(burger=>(
                    <Table.Row key={burger.id}>
                    <Table.Cell>
                      <Header as='h4'>
                            <Link to={{
                                pathname: `burgers/${burger.id}`,
                                state: burger
                                }}>
                            <Header.Content>
                            {burger.name}
                             </Header.Content>
                          </Link>

                      </Header>
                    </Table.Cell>
                    <Table.Cell>{burger.ingridients}</Table.Cell>
                    <Table.Cell>{burger.price}</Table.Cell>
                    <Table.Cell><Button color="purple">Edit</Button></Table.Cell>
                    <Table.Cell><Button color="red">Delete</Button></Table.Cell>
                  </Table.Row>

                ))}
                </Table.Body>
                </Table>
                </Container>
        )
    }
}
