import React from "react";
import { Button, Header, Icon, Modal } from 'semantic-ui-react'


export class GameDetails extends React.Component{
    constructor(props){
        super(props)
        this.state={
            data:[],
            loading: false,
            error: null,
        }

    }

    // componentDidMount(){
    //     this.fetchData()
    // }
    // fetchData(){
    //     fetch("/levels.json")
    //         .then(resp=>resp.json())
    //         .then(resp=>
    //            this.setState({
    //                 data: resp
    //            })
    //            ) 
    //         .catch(error=>this.setState({
    //             error:"err occ"
    //         }))  
    // }
    render(){
        console.log(this.state.data)
        const playerLVL = this.state.data
        return(
            <div>
             <Modal trigger={<Button color="blue">Details</Button>} basic size='small'>
            <Header icon='chess' content='Game Details' />
            <Modal.Content>
            <p>
            Title:  {this.props.title}<br/>
            Localization:  {this.props.localization}<br/>
            Date:  {this.props.date}<br/>
            Players:  {this.props.playerCur} /{this.props.playerMax}<br/>
        ReQuired Level:  {playerLVL[this.props.reqLvl].name}
            </p>
            </Modal.Content>
            <Modal.Actions>
            <Button basic color='red' inverted>
                <Icon name='remove' /> Report
            </Button>
            <Button color='green' inverted>
                <Icon name='checkmark' /> Join
            </Button>
            </Modal.Actions>
        </Modal>

            </div>
        )
    }
}
