import React from "react";
import BurgersForm from "./BurgersForm"
import PageWrapper from "../components/PageWrapper";
import BurgersTable from "./BurgersTable";

export const BASE_URL = 'https://reast-api-b141b.firebaseio.com/';

const InitialFormState =
                {
                 name:"",
                ingridients:"",
                price:0
            }
export default class Burgers extends React.Component{
    constructor(props){
        super(props)
        this.state={
            burgers:[],
            }
        }
    handleOnSubmit=(formData)=>{
        fetch(`${BASE_URL}/burgers.json`,{
            method: "POST",
            body: JSON.stringify(formData)
        })
        .then(()=>{  
            this.fetchBurgers();
            this.setState({
                form: InitialFormState
            })
        }
        )
    }
    fetchBurgers(){
        fetch(`${BASE_URL}/burgers.json`)
        .then(resp=>resp.json())
        .then(resp=>this.setState({
            burgers: Object.keys(resp).map(i=>({ ...resp[i], id: i }))
        }))
        }
    componentDidMount(){
        this.fetchBurgers()
    }
    render(){
        const {burgers}= this.state
        return(
                <PageWrapper>
                <BurgersTable burgers={burgers}/>
                <BurgersForm onSubmit={this.handleOnSubmit}/>
                </PageWrapper>
        )
    }
}
