import React from "react";

export default class GameCard extends React.Component{
    constructor(){
        super()
        this.state={
            games:[],
            loading: false,
            error: null
        }
    }

    fetchData(){
        fetch("../../../public/kinds-of-games.json")
            .then(resp=>resp.json)
            .then(resp=>
               this.setState({
                    games: resp
               })) 
            .catch(error=>this.setState({
                error
            }))  
    }

    displayGameKind(){
        return(
            this.state.games.map(
                game=>
                <div>
            <p>{game.kindID}</p>
            <p>{game.kindName}</p>
            </div>
            )
        )
    }
    render(){
        return(
            <div>
                {this.state.error?<p>Error Occured</p>:null}

                <tittle>Game card</tittle>
                <this.displayGameKind/>


            </div>
        )
    }
}
